import React, { useState } from 'react';
import styled from 'styled-components';
import { Match as MatchType, Team } from '../types';
import Match from './Match';
import { Button, Card, Section, SectionTitle, Flex, Badge } from './styled/Common';

interface PlayInRoundProps {
  matches: MatchType[];
  onComplete: (winners: Team[]) => void;
}

const PlayInContainer = styled.div`
  max-width: 1200px;
  margin: 0 auto;
`;

const MatchesGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(400px, 1fr));
  gap: 2rem;
  margin-bottom: 2rem;

  @media (max-width: 768px) {
    grid-template-columns: 1fr;
  }
`;

const ProgressBar = styled.div`
  background: rgba(255, 255, 255, 0.1);
  border-radius: 10px;
  height: 8px;
  margin: 1rem 0;
  overflow: hidden;
`;

const ProgressFill = styled.div<{ progress: number }>`
  background: linear-gradient(135deg, #3b82f6 0%, #1d4ed8 100%);
  height: 100%;
  width: ${props => props.progress}%;
  transition: width 0.5s ease;
`;

const StatsContainer = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 2rem;
  flex-wrap: wrap;
  gap: 1rem;
`;

const StatCard = styled.div`
  background: rgba(255, 255, 255, 0.05);
  border: 1px solid rgba(255, 255, 255, 0.1);
  border-radius: 12px;
  padding: 1rem;
  text-align: center;
  min-width: 120px;
`;

const StatNumber = styled.div`
  font-size: 2rem;
  font-weight: 700;
  color: #3b82f6;
  margin-bottom: 0.25rem;
`;

const StatLabel = styled.div`
  font-size: 0.875rem;
  color: #94a3b8;
  font-weight: 500;
`;

const PlayInRound: React.FC<PlayInRoundProps> = ({ matches, onComplete }) => {
  const [selectedWinners, setSelectedWinners] = useState<(Team | null)[]>(
    new Array(matches.length).fill(null)
  );

  const handleSelectWinner = (matchIndex: number, teamId: number) => {
    const team = matches[matchIndex].team1?.id === teamId 
      ? matches[matchIndex].team1 
      : matches[matchIndex].team2;

    if (team) {
      const newWinners = [...selectedWinners];
      newWinners[matchIndex] = team;
      setSelectedWinners(newWinners);
    }
  };

  const completedMatches = selectedWinners.filter(winner => winner !== null).length;
  const progress = (completedMatches / matches.length) * 100;

  const handleComplete = () => {
    const winners = selectedWinners.filter(winner => winner !== null) as Team[];
    if (winners.length === matches.length) {
      onComplete(winners);
    }
  };

  const canComplete = completedMatches === matches.length;

  return (
    <PlayInContainer>
      <Section>
        <SectionTitle>üèÜ Play-in Tournament</SectionTitle>
        <Card>
          <p style={{ textAlign: 'center', marginBottom: '1rem', color: '#94a3b8' }}>
            Select the winners of each play-in match. 8 teams will advance to the main tournament.
          </p>

          <StatsContainer>
            <StatCard>
              <StatNumber>{completedMatches}</StatNumber>
              <StatLabel>Matches Completed</StatLabel>
            </StatCard>
            <StatCard>
              <StatNumber>{matches.length}</StatNumber>
              <StatLabel>Total Matches</StatLabel>
            </StatCard>
            <StatCard>
              <StatNumber>{Math.round(progress)}%</StatNumber>
              <StatLabel>Progress</StatLabel>
            </StatCard>
          </StatsContainer>

          <ProgressBar>
            <ProgressFill progress={progress} />
          </ProgressBar>
        </Card>
      </Section>

      <MatchesGrid>
        {matches.map((match, index) => (
          <Match
            key={match.id}
            match={{
              ...match,
              winner: selectedWinners[index] || null
            }}
            onSelectWinner={(teamId) => handleSelectWinner(index, teamId)}
            showMatchNumber
          />
        ))}
      </MatchesGrid>

      <Flex justify="center" gap="1rem">
        <Button
          variant="success"
          onClick={handleComplete}
          disabled={!canComplete}
        >
          {canComplete ? 'Complete Play-in Tournament' : `Complete ${completedMatches}/${matches.length} Matches`}
        </Button>
      </Flex>

      {canComplete && (
        <Card style={{ marginTop: '2rem', textAlign: 'center' }}>
          <h3 style={{ color: '#10b981', marginBottom: '1rem' }}>
            üéâ Play-in Tournament Complete!
          </h3>
          <p style={{ color: '#94a3b8', marginBottom: '1rem' }}>
            The following teams have advanced to the main tournament:
          </p>
          <Flex justify="center" gap="0.5rem" style={{ flexWrap: 'wrap' }}>
            {selectedWinners.map((winner, index) => (
              <Badge key={index} variant="success">
                {winner?.flag} {winner?.name}
              </Badge>
            ))}
          </Flex>
        </Card>
      )}
    </PlayInContainer>
  );
};

export default PlayInRound;
