import React from 'react';
import styled from 'styled-components';

interface BracketLinesProps {
  roundIndex: number;
  matchIndex: number;
  totalMatches: number;
  isCurrentRound: boolean;
}

const BracketLine = styled.div<{ 
  roundIndex: number;
  matchIndex: number;
  totalMatches: number;
  isCurrentRound: boolean;
}>`
  position: absolute;
  pointer-events: none;
  z-index: 1;

  /* Horizontal line to next round */
  &::before {
    content: '';
    position: absolute;
    right: -1rem;
    top: 50%;
    width: 1rem;
    height: 2px;
    background: ${props => props.isCurrentRound ? 'var(--border-color)' : 'transparent'};
    display: block;
  }

  /* Vertical line for bracket connections */
  &::after {
    content: '';
    position: absolute;
    right: -1rem;
    top: ${props => {
      const { matchIndex, totalMatches, roundIndex } = props;

      // Calculate the center position of this match
      const matchHeight = 120; // px
      const matchSpacing = 20; // px
      const totalMatchSpace = matchHeight + matchSpacing;
      const spacingMultiplier = Math.pow(2, roundIndex);
      const matchCenter = (matchIndex * spacingMultiplier * totalMatchSpace) + (matchHeight / 2);

      // Calculate the center of the corresponding match in the next round
      const nextRoundMatchIndex = Math.floor(matchIndex / 2);
      const nextRoundSpacingMultiplier = Math.pow(2, roundIndex + 1);
      const nextRoundMatchCenter = (nextRoundMatchIndex * nextRoundSpacingMultiplier * totalMatchSpace) + (matchHeight / 2);

      // Determine if this is the top or bottom match of the pair
      const isTopMatch = matchIndex % 2 === 0;

      if (isTopMatch) {
        // Top match - line goes down to center
        return '50%';
      } else {
        // Bottom match - line goes up to center
        return '0%';
      }
    }};
    width: 2px;
    height: ${props => {
      const { matchIndex, totalMatches, roundIndex } = props;

      // Calculate the distance to the center of the next round match
      const matchHeight = 120; // px
      const matchSpacing = 20; // px
      const totalMatchSpace = matchHeight + matchSpacing;
      const spacingMultiplier = Math.pow(2, roundIndex);
      const nextRoundSpacingMultiplier = Math.pow(2, roundIndex + 1);

      const isTopMatch = matchIndex % 2 === 0;

      if (isTopMatch) {
        // Top match - line extends down to center
        return '50%';
      } else {
        // Bottom match - line extends up to center
        return '50%';
      }
    }};
    background: ${props => props.isCurrentRound ? 'var(--border-color)' : 'transparent'};
    display: block;
  }
`;

const BracketLines: React.FC<BracketLinesProps> = ({ 
  roundIndex, 
  matchIndex, 
  totalMatches, 
  isCurrentRound 
}) => {
  return (
    <BracketLine
      roundIndex={roundIndex}
      matchIndex={matchIndex}
      totalMatches={totalMatches}
      isCurrentRound={isCurrentRound}
    />
  );
};

export default BracketLines;
