import React from 'react';
import styled from 'styled-components';
import { Team as TeamType } from '../types';

interface TeamProps {
  team: TeamType;
  onClick?: () => void;
  selected?: boolean;
  position?: number | null;
  size?: 'small' | 'medium' | 'large';
  showSeed?: boolean;
  showRank?: boolean;
}

const TeamContainer = styled.div<{ 
  selected?: boolean; 
  position?: number | null;
  size?: string;
  clickable?: boolean;
}>`
  display: flex;
  align-items: center;
  gap: 0.5rem;
  padding: ${props => props.size === 'small' ? '0.375rem 0.5rem' : props.size === 'large' ? '0.75rem 1rem' : '0.5rem 0.75rem'};
  background: ${props => {
    if (props.position === 1) return 'linear-gradient(135deg, rgba(251, 191, 36, 0.2) 0%, rgba(245, 158, 11, 0.3) 100%)';
    if (props.position === 2) return 'linear-gradient(135deg, rgba(156, 163, 175, 0.2) 0%, rgba(107, 114, 128, 0.3) 100%)';
    if (props.selected) return 'linear-gradient(135deg, rgba(59, 130, 246, 0.2) 0%, rgba(29, 78, 216, 0.3) 100%)';
    return 'var(--bg-card)';
  }};
  border: 2px solid ${props => {
    if (props.position === 1) return '#fbbf24';
    if (props.position === 2) return '#9ca3af';
    if (props.selected) return '#3b82f6';
    return 'var(--border-color)';
  }};
  border-radius: 12px;
  cursor: ${props => props.clickable ? 'pointer' : 'default'};
  transition: all 0.3s ease;
  min-width: ${props => props.size === 'small' ? '200px' : props.size === 'large' ? '300px' : '250px'};
  position: relative;

  &:hover {
    transform: ${props => props.clickable ? 'translateY(-2px)' : 'none'};
    box-shadow: ${props => props.clickable ? '0 8px 25px var(--shadow)' : 'none'};
    background: ${props => {
      if (props.clickable && !props.selected && !props.position) {
        return 'var(--bg-card-hover)';
      }
      if (props.position === 1) return 'linear-gradient(135deg, rgba(251, 191, 36, 0.2) 0%, rgba(245, 158, 11, 0.3) 100%)';
      if (props.position === 2) return 'linear-gradient(135deg, rgba(156, 163, 175, 0.2) 0%, rgba(107, 114, 128, 0.3) 100%)';
      if (props.selected) return 'linear-gradient(135deg, rgba(59, 130, 246, 0.2) 0%, rgba(29, 78, 216, 0.3) 100%)';
      return 'var(--bg-card)';
    }};
  }
`;

const Flag = styled.div<{ size?: string }>`
  font-size: ${props => props.size === 'small' ? '1.5rem' : props.size === 'large' ? '2.5rem' : '2rem'};
  line-height: 1;
`;

const TeamInfo = styled.div`
  flex: 1;
  min-width: 0;
`;

const TeamName = styled.div<{ size?: string }>`
  font-weight: 600;
  font-size: ${props => props.size === 'small' ? '0.875rem' : props.size === 'large' ? '1.25rem' : '1rem'};
  color: var(--text-primary);
  margin-bottom: 0.25rem;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
`;

const TeamDetails = styled.div<{ size?: string }>`
  display: flex;
  gap: 0.5rem;
  font-size: ${props => props.size === 'small' ? '0.75rem' : '0.875rem'};
  color: var(--text-secondary);
`;

const Seed = styled.span`
  background: rgba(59, 130, 246, 0.2);
  color: #3b82f6;
  padding: 0.125rem 0.375rem;
  border-radius: 4px;
  font-weight: 500;
  font-size: 0.75rem;
`;

const Rank = styled.span`
  background: rgba(245, 158, 11, 0.2);
  color: #f59e0b;
  padding: 0.125rem 0.375rem;
  border-radius: 4px;
  font-weight: 500;
  font-size: 0.75rem;
`;

const PositionPill = styled.div<{ position: number }>`
  background: ${props => props.position === 1 
    ? 'linear-gradient(135deg, #fbbf24 0%, #f59e0b 100%)' 
    : 'linear-gradient(135deg, #9ca3af 0%, #6b7280 100%)'
  };
  color: white;
  padding: 0.25rem 0.75rem;
  border-radius: 20px;
  font-size: 0.75rem;
  font-weight: 600;
  margin-left: auto;
  display: flex;
  align-items: center;
  gap: 0.25rem;
  cursor: pointer;
  transition: all 0.2s ease;

  &:hover {
    transform: scale(1.05);
  }
`;

const Team: React.FC<TeamProps> = ({ 
  team, 
  onClick, 
  selected = false, 
  position = null,
  size = 'medium',
  showSeed = false,
  showRank = false
}) => {
  return (
    <TeamContainer 
      selected={selected} 
      position={position}
      size={size}
      clickable={!!onClick}
      onClick={onClick}
    >
      <Flag size={size}>{team.flag}</Flag>
      <TeamInfo>
        <TeamName size={size}>{team.name}</TeamName>
        <TeamDetails size={size}>
          {showSeed && <Seed>#{team.seed}</Seed>}
          {showRank && <Rank>#{team.fifaRank}</Rank>}
        </TeamDetails>
      </TeamInfo>
      {position && (
        <PositionPill position={position}>
          {position === 1 ? 'ðŸ¥‡ 1st' : 'ðŸ¥ˆ 2nd'}
        </PositionPill>
      )}
    </TeamContainer>
  );
};

export default Team;
