import React from 'react';
import styled from 'styled-components';
import { Team as TeamType } from '../types';

interface TeamProps {
  team: TeamType;
  onClick?: () => void;
  selected?: boolean;
  winner?: boolean;
  size?: 'small' | 'medium' | 'large';
  showSeed?: boolean;
  showRank?: boolean;
}

const TeamContainer = styled.div<{ 
  selected?: boolean; 
  winner?: boolean; 
  size?: string;
  clickable?: boolean;
}>`
  display: flex;
  align-items: center;
  gap: 0.75rem;
  padding: ${props => props.size === 'small' ? '0.5rem' : props.size === 'large' ? '1rem 1.5rem' : '0.75rem 1rem'};
  background: ${props => {
    if (props.winner) return 'linear-gradient(135deg, rgba(16, 185, 129, 0.2) 0%, rgba(5, 150, 105, 0.3) 100%)';
    if (props.selected) return 'linear-gradient(135deg, rgba(59, 130, 246, 0.2) 0%, rgba(29, 78, 216, 0.3) 100%)';
    return 'rgba(255, 255, 255, 0.05)';
  }};
  border: 2px solid ${props => {
    if (props.winner) return '#10b981';
    if (props.selected) return '#3b82f6';
    return 'rgba(255, 255, 255, 0.1)';
  }};
  border-radius: 12px;
  cursor: ${props => props.clickable ? 'pointer' : 'default'};
  transition: all 0.3s ease;
  min-width: ${props => props.size === 'small' ? '200px' : props.size === 'large' ? '300px' : '250px'};

  &:hover {
    transform: ${props => props.clickable ? 'translateY(-2px)' : 'none'};
    box-shadow: ${props => props.clickable ? '0 8px 25px rgba(0, 0, 0, 0.3)' : 'none'};
    background: ${props => {
      if (props.clickable && !props.selected && !props.winner) {
        return 'rgba(255, 255, 255, 0.1)';
      }
      return props.background;
    }};
  }
`;

const Flag = styled.div<{ size?: string }>`
  font-size: ${props => props.size === 'small' ? '1.5rem' : props.size === 'large' ? '2.5rem' : '2rem'};
  line-height: 1;
`;

const TeamInfo = styled.div`
  flex: 1;
  min-width: 0;
`;

const TeamName = styled.div<{ size?: string }>`
  font-weight: 600;
  font-size: ${props => props.size === 'small' ? '0.875rem' : props.size === 'large' ? '1.25rem' : '1rem'};
  color: #ffffff;
  margin-bottom: 0.25rem;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
`;

const TeamDetails = styled.div<{ size?: string }>`
  display: flex;
  gap: 0.5rem;
  font-size: ${props => props.size === 'small' ? '0.75rem' : '0.875rem'};
  color: #94a3b8;
`;

const Seed = styled.span`
  background: rgba(59, 130, 246, 0.2);
  color: #3b82f6;
  padding: 0.125rem 0.375rem;
  border-radius: 4px;
  font-weight: 500;
  font-size: 0.75rem;
`;

const Rank = styled.span`
  background: rgba(245, 158, 11, 0.2);
  color: #f59e0b;
  padding: 0.125rem 0.375rem;
  border-radius: 4px;
  font-weight: 500;
  font-size: 0.75rem;
`;

const WinnerBadge = styled.div`
  background: linear-gradient(135deg, #059669 0%, #10b981 100%);
  color: white;
  padding: 0.25rem 0.5rem;
  border-radius: 6px;
  font-size: 0.75rem;
  font-weight: 600;
  margin-left: auto;
`;

const Team: React.FC<TeamProps> = ({ 
  team, 
  onClick, 
  selected = false, 
  winner = false, 
  size = 'medium',
  showSeed = false,
  showRank = false
}) => {
  return (
    <TeamContainer 
      selected={selected} 
      winner={winner} 
      size={size}
      clickable={!!onClick}
      onClick={onClick}
    >
      <Flag size={size}>{team.flag}</Flag>
      <TeamInfo>
        <TeamName size={size}>{team.name}</TeamName>
        <TeamDetails size={size}>
          {showSeed && <Seed>#{team.seed}</Seed>}
          {showRank && <Rank>#{team.fifaRank}</Rank>}
        </TeamDetails>
      </TeamInfo>
      {winner && <WinnerBadge>WINNER</WinnerBadge>}
    </TeamContainer>
  );
};

export default Team;
