import React from 'react';
import styled from 'styled-components';
import { Match as MatchType } from '../types';
import Team from './Team';

interface MatchProps {
  match: MatchType;
  onSelectWinner?: (teamId: number) => void;
  disabled?: boolean;
  showMatchNumber?: boolean;
}

const MatchContainer = styled.div<{ disabled?: boolean }>`
  background: rgba(255, 255, 255, 0.03);
  border: 1px solid rgba(255, 255, 255, 0.1);
  border-radius: 16px;
  padding: 1.5rem;
  margin-bottom: 1rem;
  transition: all 0.3s ease;
  opacity: ${props => props.disabled ? 0.6 : 1};

  &:hover {
    transform: ${props => props.disabled ? 'none' : 'translateY(-2px)'};
    box-shadow: ${props => props.disabled ? 'none' : '0 8px 25px rgba(0, 0, 0, 0.2)'};
  }
`;

const MatchHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 1rem;
`;

const MatchNumber = styled.div`
  background: linear-gradient(135deg, #3b82f6 0%, #1d4ed8 100%);
  color: white;
  padding: 0.5rem 1rem;
  border-radius: 20px;
  font-weight: 600;
  font-size: 0.875rem;
`;

const MatchRound = styled.div`
  color: #94a3b8;
  font-size: 0.875rem;
  font-weight: 500;
`;

const TeamsContainer = styled.div`
  display: flex;
  flex-direction: column;
  gap: 1rem;
`;

const VS = styled.div`
  text-align: center;
  color: #64748b;
  font-weight: 600;
  font-size: 1.25rem;
  margin: 0.5rem 0;
  position: relative;

  &::before,
  &::after {
    content: '';
    position: absolute;
    top: 50%;
    width: 30%;
    height: 1px;
    background: rgba(255, 255, 255, 0.2);
  }

  &::before {
    left: 0;
  }

  &::after {
    right: 0;
  }
`;

const WinnerIndicator = styled.div`
  background: linear-gradient(135deg, #059669 0%, #10b981 100%);
  color: white;
  padding: 0.5rem 1rem;
  border-radius: 8px;
  font-weight: 600;
  text-align: center;
  margin-top: 1rem;
  font-size: 0.875rem;
`;

const Match: React.FC<MatchProps> = ({ 
  match, 
  onSelectWinner, 
  disabled = false,
  showMatchNumber = true
}) => {
  const handleTeamClick = (teamId: number) => {
    if (!disabled && onSelectWinner) {
      onSelectWinner(teamId);
    }
  };

  return (
    <MatchContainer disabled={disabled}>
      <MatchHeader>
        {showMatchNumber && (
          <MatchNumber>
            {match.isPlayIn ? 'Play-in' : 'Match'} #{match.matchNumber}
          </MatchNumber>
        )}
        <MatchRound>
          {match.isPlayIn ? 'Play-in Tournament' : `Round ${match.round}`}
        </MatchRound>
      </MatchHeader>

      <TeamsContainer>
        {match.team1 && (
          <Team
            team={match.team1}
            onClick={() => handleTeamClick(match.team1!.id)}
            selected={match.winner?.id === match.team1.id}
            position={match.winner?.id === match.team1.id ? 1 : null}
            size="medium"
            showSeed
          />
        )}

        {match.team1 && match.team2 && <VS>VS</VS>}

        {match.team2 && (
          <Team
            team={match.team2}
            onClick={() => handleTeamClick(match.team2!.id)}
            selected={match.winner?.id === match.team2.id}
            position={match.winner?.id === match.team2.id ? 1 : null}
            size="medium"
            showSeed
          />
        )}
      </TeamsContainer>

      {match.winner && (
        <WinnerIndicator>
          üèÜ {match.winner.name} advances to next round
        </WinnerIndicator>
      )}
    </MatchContainer>
  );
};

export default Match;
